config-descriptions.content:
{
  "default_model": "Sets the default LLM model to use for grove commands that require AI generation (e.g., changelog generation). This model will be used unless explicitly overridden by command-line flags."
}

config-examples.content:
{
  "default_model": {
    "example": "[llm]\ndefault_model = \"gemini-2.0-flash\"",
    "description": "Sets Gemini 2.0 Flash as the default model for AI-powered grove commands."
  }
}
